    public static List<Movie> getSeedData() {
        try {
            JSONArray rawSeedData = new JSONArray(seedData);
            return extractMoviesFromJSONArray(rawSeedData);
        } catch (JSONException e) {
            e.printStackTrace();
            return new ArrayList<>();
        }
    }

    private static List<Movie> extractMoviesFromJSONArray(JSONArray rawSeedData) throws JSONException {
        List<Movie> movies = new ArrayList<>();
        for(int ii = 0; ii < rawSeedData.length(); ii++) {
            JSONObject rawMovie = rawSeedData.getJSONObject(ii);
            Movie movie = extractMovieFromJSONObject(rawMovie);
            movies.add(movie);
        }

        return movies;
    }

    private static Movie extractMovieFromJSONObject(JSONObject rawMovie) throws JSONException {
        Movie movie = new Movie();
        movie.setTitle(rawMovie.getString("title"));
        movie.setBanner(rawMovie.getString("banner"));
        movie.setReleasedYear(rawMovie.getString("released_year"));
        movie.setReviewScore(rawMovie.getInt("review_score"));
        movie.setOverview(rawMovie.getString("overview"));
        movie.setRevenue(rawMovie.getString("revenue"));
        movie.setBudget(rawMovie.getString("budget"));
        movie.setRuntime(rawMovie.getString("runtime"));
        movie.setOriginalLanguage(rawMovie.getString("original_language"));

        JSONArray rawCasts = rawMovie.getJSONArray("top_billed_cast");
        List<Cast> casts = extractCastsFromJSONArray(rawCasts);
        movie.setTopBilledCasts(casts);
        return movie;
    }

    private static List<Cast> extractCastsFromJSONArray(JSONArray rawCasts) throws JSONException {
        List<Cast> casts = new ArrayList<>();
        for(int jj = 0; jj < rawCasts.length(); jj++) {
            JSONObject rawCast = rawCasts.getJSONObject(jj);
            Cast cast = extractCastFromJSONObject(rawCast);
            casts.add(cast);
        }

        return casts;
    }

    private static Cast extractCastFromJSONObject(JSONObject rawCast) throws JSONException {
        Cast cast = new Cast();
        cast.setName(rawCast.getString("top_billed_cast_name"));
        cast.setCharacterName(rawCast.getString("top_billed_cast_character_name"));
        cast.setImageSrc(rawCast.getString("top_billed_cast_img_src"));

        return cast;
    }
